import replaceIcon from './components/backtop/replaceIcon';
import updateItems from './components/carousel/updateItems';
import replaceSkeleton from './components/skeleton/replaceSkeleton';
import calcPaneInstances from './components/tabs/calcPaneInstances';
export default function (components) {
    return components.map(function (com) {
        if (com.name.includes('Tabs')) {
            com.methods.calcPaneInstances = calcPaneInstances;
        }
        if (com.name.includes('Carousel')) {
            if (com.name === 'rvCarousel') {
                com.methods.updateItems = updateItems;
            }
        }
        if (com.name.includes('Backtop')) {
            com.render = replaceIcon;
        }
        if (com.name === 'rvSkeleton') {
            com.render = replaceSkeleton;
        }
        return com;
    });
}
